// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

/// @author Syndicate Inc.
/// @notice Core module for owner functionality
abstract contract SyndicateOwnerLogic is Ownable {
    // Use a consistent denominator for basis point calculations
    uint256 public constant BASIS_POINTS_DENOMINATOR = 10000;
    // Address of the Gnosis Safe where the Syndicate distribution share is collected.
    // Needs to be private so inheriting contracts cannot change the address.
    address private syndicateFeeAddress =
        0x40dB79f7f18C468c6552538b488c58B5E72bf4D9;

    /// @notice Used to initialize the SyndicateOwnershipLogic contract.
    /// @param owner Address of contract owner upon deployment. Typically should
    /// be `syndicateFeeAddress`.
    /// @notice Passing in 0x0 for owner will fail as it is checked by
    /// `transferOwnership`.
    constructor(address owner) Ownable() {
        // Reduces the risk of us forgetting to transfer ownership to Syndcate's
        // Gnosis safe or having it fail after deployment.
        // As a side effect, ensures the owner is never unset.
        transferOwnership(owner);
    }

    // Events------------------------------------------------------------------

    /// @notice The owner updates the contract fee address.
    event SyndicateFeeAddressUpdated(address indexed contractFeeAddress);

    /// @notice getter function for syndicate fee address.
    /// @dev This function is needed to allow inheriting contracts to
    /// only be able to view but not change syndicateFeeAddress without
    /// going through ownerSetSyndicateFeeAddress
    function getSyndicateFeeAddress() public view returns (address) {
        return syndicateFeeAddress;
    }

    // Owner functions----------------------------------------------------------

    /// @notice set a new fee address for the contract function that can only
    /// be triggered by the contract owner.
    /// @param feeAddress The owner's new contract fee address
    function ownerSetSyndicateFeeAddress(address feeAddress) public onlyOwner {
        syndicateFeeAddress = feeAddress;

        emit SyndicateFeeAddressUpdated(feeAddress);
    }

    /// @notice Transfers ownership to the provided address.
    /// @param newOwner The new owner's address.
    function ownerTransferOwnership(address newOwner) public onlyOwner {
        transferOwnership(newOwner);
    }

    /// @notice Renounce ownership of the contract
    function ownerRenounceOwnership() public onlyOwner {
        super.renounceOwnership();
    }
}

